package net.tqft.toolkit.algebra.fusion

import org.junit.runner.RunWith
import org.scalatest.junit.JUnitRunner
import org.scalatest._

@RunWith(classOf[JUnitRunner])
class ExoticSubfactorsTest extends FlatSpec with Matchers {

  val H1 = PartialFusionRing("4,0 2 111121110121221111110111010 35.72")
  val H2 = PartialFusionRing("4,1 1 11100111011111000100010001111011100111011000000100111011111011100010001000001000001001000000001000000010010000010000000000010 35.725")
  val AH1 = PartialFusionRing("6,0 6 11000111100101001121000111111012131111211324201120010101112101111121310111001121132421213124362121210001101120011101212110011 267.939")
  val AH2 = PartialFusionRing("7,1 3  267.939")
  val EH1 = PartialFusionRing("6,0 7 11000111000111100121001101110011111112310133201121011111123112453135631133200121013321356323674123420011001121113321234201221 570.247")
  val EH2 = PartialFusionRing("6,1 4  570.247")

  "the rank 4 Haagerup category" should "have a valid ancestry" in { 
    H1.verifyAncestry should be(true)
  }
  "the rank 6 Haagerup category" should "have a valid ancestry" in {
    H2.verifyAncestry should be(true)
  }
  "the rank 6 Asaeda-Haagerup category" should "have a valid ancestry" in {
    AH1.verifyAncestry should be(true)
  }
  "the rank 9 Asaeda-Haagerup category" should "have a valid ancestry" in {
    AH2.verifyAncestry should be(true)
  }
  "the rank 6 extended Haagerup category" should "have a valid ancestry" in {
    EH1.verifyAncestry should be(true)
  }
  "the rank 8 extended Haagerup category" should "have a valid ancestry" in {
    EH2.verifyAncestry should be(true)
  }
}